    private void tblHistoricoMouseClicked(java.awt.event.MouseEvent evt)//GEN-FIRST:event_tblHistoricoMouseClicked
    {//GEN-HEADEREND:event_tblHistoricoMouseClicked
        int linha = tblHistorico.getSelectedRow();
        Estaticos.clima.setId((int) tblHistorico.getValueAt(linha, 0));
        Estaticos.clima.setTemperatura((String) tblHistorico.getValueAt(linha, 1));
        Estaticos.clima.setUmidade((String) tblHistorico.getValueAt(linha, 2));
        Estaticos.clima.setPrecipitacao((String) tblHistorico.getValueAt(linha, 3));
        Estaticos.clima.setDataRegistro((String) tblHistorico.getValueAt(linha, 4));
    }//GEN-LAST:event_tblHistoricoMouseClicked

