    private void preencherTabela()
    {
        DefaultTableModel modelo = (DefaultTableModel) tblHistorico.getModel();
        modelo.setNumRows(0);
        for (Clima c : Estaticos.listaClima)
        {
            modelo.addRow(new Object[]
            {
                c.getId(),
                c.getTemperatura(),
                c.getUmidade(),
                c.getPrecipitacao(),
                c.getDataRegistro(),
            });
        }
    }

